var dnet;
(function(){
	dnet={
		get: function (url,callback,async){
			return dnet.XMLHR('GET',url,async,callback);
		},
		post: function (url,callback,async){
			return dnet.XMLHR('POST',url,async,callback);
		},
		XMLHR: function (method,url,async,callback){
			async=async || true;
			var req=new XMLHttpRequest();
			req.open(method,url,async);
			req.send();	
			req.onreadystatechange=function(e){
				if (callback) callback(req);	
			}
			return req;
		}	
	}	
})();
if (typeof exports !== 'undefined') exports.dnet=dnet;
console.log('loaded dnet');
